                                new SpectrumPeak() { Mass = nl_SN2, Intensity = 0.01 },
                                new SpectrumPeak() { Mass = nl_NS2_H2O, Intensity = 0.01 },
                                new SpectrumPeak() { Mass = nl_HD_SN1, Intensity = 0.01 },
                                new SpectrumPeak() { Mass = nl_HD_SN2, Intensity = 0.01 }
