                            //        var molecule = LipidMsmsCharacterizationUtility.getEtherPhospholipidMoleculeObjAsLevel2("PE", LbmClass.EtherPE, sn1Carbon, sn1Double,
                            //        sn2Carbon, sn2Double, averageIntensity, "e");
                            //        candidates.Add(molecule);
