                    var candidates = new List<LipidMolecule>();
                    for (int sn1Carbon = minSnCarbon; sn1Carbon <= maxSnCarbon; sn1Carbon++)
                    {
                        if (sn1Carbon < 10) continue;
                        for (int sn1Double = minSnDoubleBond; sn1Double <= maxSnDoubleBond; sn1Double++)
