            var LPHex = new Dictionary<string, string>()
                 { {"BRSLPHex","SG 28:2;O;Hex;LPA" },{"CASLPHex","SG 28:1;O;Hex;LPA" },{"CSLPHex","SG 27:1;O;Hex;LPA" },{"SISLPHex","SG 29:1;O;Hex;LPA"},{"STSLPHex","SG 29:2;O;Hex;LPA"} };
