                            var NL_sn1 = diagnosticMz - sn1alkyl + Proton;
                            var sn1_rearrange = sn1alkyl + 139.00290 + MassDiffDictionary.HydrogenMass * 2;//sn1(ether) + C2H5NO4P + proton 

